import android.app.Application;

public class SobTest {
    private CampingRepository mRepository;
    public static void ejecutar(Application application) {
        CampingRepository mRepository = new CampingRepository(application);
        long fallo = 0;
        int i = 0;        String descripcion = "sobrecarga";
        while (fallo != -1) {
            Parcela pAux = new Parcela("parcela", descripcion, 5.5, 50);
            fallo = mRepository.insert(pAux);
            android.util.Log.d("Tama√±o", String.valueOf(descripcion.length()));
            mRepository.delete(pAux);
            descripcion += "";
            i++;
        }
    }
}
